// Load schema from file
const fs = require('fs');
const path = require('path');

const schemaPath = '/home/node/.n8n/claude-flow/contracts/tool-spec.schema.json';
let schema;

try {
  schema = JSON.parse(fs.readFileSync(schemaPath, 'utf8'));
} catch (error) {
  return {
    "valid": false,
    "error": "schema_load_failed",
    "message": "Failed to load tool-spec.schema.json",
    "details": error.message
  };
}

return {
  "schema": schema,
  "toolspec": $input.item.json.toolspec,
  "intent": $input.item.json.intent
};

// Validate using Ajv
const Ajv = require('ajv');
const ajv = new Ajv({
  strict: true,
  strictSchema: true,
  strictTypes: true,
  strictRequired: true,
  removeAdditional: false,
  useDefaults: false,
  coerceTypes: false
});

const schema = $input.item.json.schema;
const toolspec = $input.item.json.toolspec;

const validate = ajv.compile(schema);
const valid = validate(toolspec);

if (valid) {
  return {
    "valid": true,
    "toolspec": toolspec,
    "intent": $input.item.json.intent
  };
} else {
  return {
    "valid": false,
    "error": "schema_validation_failed",
    "message": "ToolSpec does not conform to schema",
    "errors": validate.errors.map(err => ({
      "path": err.instancePath || "root",
      "keyword": err.keyword,
      "message": err.message,
      "params": err.params
    }))
  };
}

// Cross-validate ToolSpec against original Intent
const toolspec = $json.toolspec;
const intent = $json.intent;
const issues = [];

// Check: ToolSpec execution_mode aligns with intent
if (intent.validation_level === "strict") {
  // In strict mode, remote execution is preferred for compute-heavy operations
  if (toolspec.execution_mode === "local" && toolspec.resource_class === "compute") {
    issues.push({
      "field": "execution_mode",
      "constraint": "strict_compute_remote",
      "message": "Strict mode: compute tools should be remote",
      "severity": "warning"
    });
  }
}

// Check: Rollback strategy matches intent requirement
if (intent.rollback_required && toolspec.rollback_strategy === "none") {
  issues.push({
    "field": "rollback_strategy",
    "constraint": "rollback_required",
    "message": "Intent requires rollback but ToolSpec has none",
    "severity": "error"
  });
}

// Check: No forbidden actions in side_effects
if (intent.forbidden_actions && toolspec.side_effects) {
  const actionMap = {
    "delete_data": "file_delete",
    "modify_production": "state_mutation",
    "external_network_call": "network_request",
    "install_dependencies": "state_mutation",
    "modify_system_config": "state_mutation",
    "create_user_accounts": "database_write",
    "grant_permissions": "database_write",
    "disable_security": "state_mutation"
  };

  for (const forbidden of intent.forbidden_actions) {
    const mappedEffect = actionMap[forbidden];
    if (mappedEffect && toolspec.side_effects.some(se => se.effect_type === mappedEffect)) {
      issues.push({
        "field": "side_effects",
        "constraint": "forbidden_action",
        "message": `ToolSpec includes forbidden action: ${forbidden}`,
        "severity": "error",
        "forbidden_action": forbidden
      });
    }
  }
}

// Check: ToolSpec outputs satisfy intent.required_outputs
if (intent.required_outputs && intent.required_outputs.length > 0) {
  // This is a heuristic check - full validation would require schema matching
  const outputTypes = toolspec.output_schema?.properties || {};
  const missing = [];

  for (const required of intent.required_outputs) {
    if (!Object.keys(outputTypes).includes(required.artifact_type)) {
      missing.push(required.artifact_type);
    }
  }

  if (missing.length > 0) {
    issues.push({
      "field": "output_schema",
      "constraint": "required_outputs",
      "message": `ToolSpec may not produce required outputs: ${missing.join(", ")}`,
      "severity": "warning"
    });
  }
}

// Check: Credentials are IDs only (no secrets)
if (toolspec.credentials_required) {
  const credentialIdPattern = /^[A-Z0-9_]+$/;
  for (const cred of toolspec.credentials_required) {
    if (!credentialIdPattern.test(cred)) {
      issues.push({
        "field": "credentials_required",
        "constraint": "credential_id_only",
        "message": `Invalid credential ID format: ${cred}`,
        "severity": "error"
      });
    }
  }
}

// Check: Timeout within bounds
if (toolspec.timeout_seconds < 1 || toolspec.timeout_seconds > 3600) {
  issues.push({
    "field": "timeout_seconds",
    "constraint": "timeout_bounds",
    "message": "Timeout must be between 1 and 3600 seconds",
    "severity": "error"
  });
}

return {
  "valid": issues.filter(i => i.severity === "error").length === 0,
  "toolspec": toolspec,
  "intent": intent,
  "issues": issues,
  "passed": issues.length === 0
};

return {
  "valid": false,
  "error": "cross_validation_failed",
  "message": "ToolSpec violated Intent constraints",
  "errors": $json.issues,
  "intent_id": $json.intent?.intent_id || null,
  "tool_id": $json.toolspec?.tool_id || null
};

